package y2021.sep;

public class Solution0920 {
    char[] sArr;
    char[] tArr;
    Integer[][] cache;

    public int numDistinct(String s, String t) {
        cache = new Integer[s.length() + 1][t.length() + 1];

        tArr = t.toCharArray();
        sArr = s.toCharArray();

        return find(0, 0, sArr.length, 0);
    }

    private int find(int findIndex, int start, int end, int cnt) {
        if (cache[start][cnt] != null) {
            return cache[start][cnt];
        }
        if (findIndex >= tArr.length) {
            cache[start][cnt] = 0;
            return 0;
        }

        char findCh = tArr[findIndex];
        int i = start;
        int result = 0;
        for (; i < end; i++) {
            if (sArr[i] == findCh) {
                //System.out.println("index: " + i + ", ch: " + sArr[i]);
                if ((cnt + 1) == tArr.length) {
                    result++;
                } else {
                    int subCnt = find(findIndex + 1, i + 1, end, cnt + 1);
                    result += subCnt;
                    cache[i + 1][cnt + 1] = subCnt;
                }
            }
        }
        //System.out.println(Arrays.deepToString(cache));

        return result;
    }

    public int numDistinct2(String S, String T) {
        // array creation
        int[][] mem = new int[T.length()+1][S.length()+1];

        // filling the first row: with 1s
        for(int j=0; j<=S.length(); j++) {
            mem[0][j] = 1;
        }

        // the first column is 0 by default in every other rows but the first, which we need.

        for(int i=0; i<T.length(); i++) {
            for(int j=0; j<S.length(); j++) {
                if(T.charAt(i) == S.charAt(j)) {
                    mem[i+1][j+1] = mem[i][j] + mem[i+1][j];
                } else {
                    mem[i+1][j+1] = mem[i+1][j];
                }
            }
        }

        return mem[T.length()][S.length()];
    }

    public static void main(String[] args) {
        Solution0920 s = new Solution0920();
        // System.out.println(s.numDistinct("babgbag", "bag"));
        // System.out.println(s.numDistinct("rabbbit", "rabbit")); // 3
        // Status: Time Limit Exceeded
        long before = System.currentTimeMillis();
        System.out.println(s.numDistinct("", "rwmimatmhydhbujebqehjprrwfkoebcxxqfktayaaeheys"));
        System.out.println("time: " + (System.currentTimeMillis() - before));
        before = System.currentTimeMillis();
        System.out.println(s.numDistinct2("", "rwmimatmhydhbujebqehjprrwfkoebcxxqfktayaaeheys"));
        System.out.println("time: " + (System.currentTimeMillis() - before));

    }
}

